package com.sposnor.intellisense.sponsorintellisense.data.model;

import java.io.IOException;
import java.util.Arrays;

import com.sun.org.apache.xerces.internal.impl.dv.util.Base64;

public class SponseeReport {

	private String uniqueId;
	
	private String studentName;
	
	private String dateOfBirth;
	
	private String gender;
	
	private String hobby;
	
	private String projectName;
	
	private String agencyName;
	
	private String grade;
	
	private String favColor;
	
	private String favGame;
	
	private String nameOfGuardian;
	
	private String occupationOfGuardian;
	
	private String baseLanguage;
	
	private String cluster;
		
	private byte[] profilePicture;
	
	private String address;
	
	
	public String getAddress() {
		return address;
	}

	public void setAddress(String address) {
		this.address = address;
	}

	public String getCluster() {
		return this.cluster;
	}

	public void setCluster(String cluster) {
		this.cluster = cluster;
	}

	public String getGrade() {
		return this.grade;
	}

	public void setGrade(String grade) {
		this.grade = grade;
	}

	public String getFavColor() {
		return this.favColor;
	}

	public void setFavColor(String favColor) {
		this.favColor = favColor;
	}

	public String getFavGame() {
		return this.favGame;
	}

	public void setFavGame(String favGame) {
		this.favGame = favGame;
	}

	public String getNameOfGuardian() {
		return this.nameOfGuardian;
	}

	public void setNameOfGuardian(String nameOfGuardian) {
		this.nameOfGuardian = nameOfGuardian;
	}

	public String getOccupationOfGuardian() {
		return this.occupationOfGuardian;
	}

	public void setOccupationOfGuardian(String occupationOfGuardian) {
		this.occupationOfGuardian = occupationOfGuardian;
	}

	public String getBaseLanguage() {
		return this.baseLanguage;
	}

	public void setBaseLanguage(String baseLanguage) {
		this.baseLanguage = baseLanguage;
	}

	public String getProfilePicture() throws IOException {
		String encodedImage;
		byte[] defaultPic = javax.xml.bind.DatatypeConverter.parseHexBinary("");
		if(null == profilePicture) {
			encodedImage = Base64.encode(defaultPic);
			return encodedImage; 
		}
		encodedImage = Base64.encode(this.profilePicture);
		return encodedImage; 
	}
	

	public void setProfilePicture(byte[] profilePicture) {
		this.profilePicture = profilePicture;
	}	

	/*public byte[] getProfilePicture() {
		return profilePicture;
	}*/

	public String getUniqueId() {
		return uniqueId;
	}

	public void setUniqueId(String uniqueId) {
		this.uniqueId = uniqueId;
	}

	public String getStudentName() {
		return studentName;
	}

	public void setStudentName(String studentName) {
		this.studentName = studentName;
	}

	public String getDateOfBirth() {
		return dateOfBirth;
	}

	public void setDateOfBirth(String dateOfBirth) {
		this.dateOfBirth = dateOfBirth;
	}

	public String getGender() {
		if(gender.equalsIgnoreCase("M")) {
			return "Male";
		}else {
			return "Female";
		}
	}

	public void setGender(String gender) {
		this.gender = gender;
	}

	public String getHobby() {
		return this.hobby;
	}

	public void setHobby(String hobby) {
		this.hobby = hobby;
	}

	public String getProjectName() {
		return projectName;
	}

	public void setProjectName(String projectName) {
		this.projectName = projectName;
	}

	public String getAgencyName() {
		return agencyName;
	}

	public void setAgencyName(String agencyName) {
		this.agencyName = agencyName;
	}

	@Override
	public String toString() {
		return "SponseeReport [uniqueId=" + uniqueId + ", studentName=" + studentName + ", dateOfBirth=" + dateOfBirth
				+ ", gender=" + gender + ", hobby=" + hobby + ", projectName=" + projectName + ", agencyName="
				+ agencyName + ", grade=" + grade + ", favColor=" + favColor + ", favGame=" + favGame
				+ ", nameOfGuardian=" + nameOfGuardian + ", occupationOfGuardian=" + occupationOfGuardian
				+ ", baseLanguage=" + baseLanguage + ", cluster=" + cluster + ",  address=" + address + "]";
	}


}
